"use strict";(self.webpackChunkdocs_sikt_no=self.webpackChunkdocs_sikt_no||[]).push([[1525],{8544:(e,n,t)=>{t.r(n),t.d(n,{assets:()=>d,contentTitle:()=>i,default:()=>m,frontMatter:()=>a,metadata:()=>l,toc:()=>o});var r=t(5893),s=t(1151);const a={title:"Sanntidsoppdatering"},i=void 0,l={id:"datadeling/veiledere/annet/anskaffelse/sanntidsoppdatering",title:"Sanntidsoppdatering",description:"Meldingsk\xf8er kan benyttes til forskjellige form\xe5l, men i utgangspunktet skiller vi mellom tynne og tykke meldinger. Det vil si om innholdet i meldingen er informasjonsb\xe6rende, eller om innholdet i meldingen bare er en peker til der informasjonen er \xe5 finne. I IntArk har vi valgt \xe5 g\xe5 for tynne meldinger, slik at mange typer meldinger kan g\xe5 til samme k\xf8. Granulert tilgangskontroll gj\xf8res i WS. Alternativet (litt satt p\xe5 spissen) er en k\xf8 per meldingstype, per konsument, noe som vil \xf8ke kompleksiteten.",source:"@site/docs/datadeling/veiledere/annet/anskaffelse/sanntidsoppdatering.md",sourceDirName:"datadeling/veiledere/annet/anskaffelse",slug:"/datadeling/veiledere/annet/anskaffelse/sanntidsoppdatering",permalink:"/docs/datadeling/veiledere/annet/anskaffelse/sanntidsoppdatering",draft:!1,unlisted:!1,editUrl:"https://github.com/sikt-no/docs/tree/master/docs/datadeling/veiledere/annet/anskaffelse/sanntidsoppdatering.md",tags:[],version:"current",lastUpdatedAt:1709023516,formattedLastUpdatedAt:"Feb 27, 2024",frontMatter:{title:"Sanntidsoppdatering"},sidebar:"datadeling",previous:{title:"Masterdata og verdikjeder",permalink:"/docs/datadeling/veiledere/annet/anskaffelse/masterdata-verdikjeder"},next:{title:"Web Services for Dummies",permalink:"/docs/datadeling/veiledere/annet/anskaffelse/webservicefordummies"}},d={},o=[];function g(e){const n={li:"li",p:"p",ul:"ul",...(0,s.a)(),...e.components};return(0,r.jsxs)(r.Fragment,{children:[(0,r.jsx)(n.p,{children:"Meldingsk\xf8er kan benyttes til forskjellige form\xe5l, men i utgangspunktet skiller vi mellom tynne og tykke meldinger. Det vil si om innholdet i meldingen er informasjonsb\xe6rende, eller om innholdet i meldingen bare er en peker til der informasjonen er \xe5 finne. I IntArk har vi valgt \xe5 g\xe5 for tynne meldinger, slik at mange typer meldinger kan g\xe5 til samme k\xf8. Granulert tilgangskontroll gj\xf8res i WS. Alternativet (litt satt p\xe5 spissen) er en k\xf8 per meldingstype, per konsument, noe som vil \xf8ke kompleksiteten."}),"\n",(0,r.jsx)(n.p,{children:"Akkurat som med WS, er det aspekter rundt sanntidsoppdatering og meldingsk\xf8 man m\xe5 ta i betraktning n\xe5r man skal anskaffe eller integrere."}),"\n",(0,r.jsxs)(n.ul,{children:["\n",(0,r.jsx)(n.li,{children:"Sanntidsoppdatering er ikke synonymt med meldingsk\xf8. Begrepet brukes om andre teknologier (som RSS, SOAP og Web push), om tjenester som deler datalager eller benytter katalogtjenester i bakgrunn."}),"\n",(0,r.jsx)(n.li,{children:"Det er p\xe5 ingen m\xe5te selvsagt at programvare man anskaffer kommer med st\xf8tte for verken \xe5 sende eller hente meldinger til MQ. Dessuten er det vanligste (n\xe5r funksjonaliteten tilbys) at systemet sender tykke meldinger."}),"\n",(0,r.jsx)(n.li,{children:"For \xe5 selv bygge noe som sender ut meldinger ved endring, b\xf8r applikasjonen v\xe6re hendelsesbasert. Dvs. at data sendes (fra bruker/presentasjonslag) gjennom standardiserte API som gj\xf8r at man har kontroll p\xe5 akkurat hvor i databasen/datalageret endring av data skjer henger sammen med hverandre, og ikke m\xe5 gj\xf8res med logiske valideringer (gjetninger) i database/-lager ut fra tidspunkt eller annet."}),"\n",(0,r.jsx)(n.li,{children:"Sanntidsoppdatering av skytjenester skjer som oftest gjennom at man m\xe5 dytte data til dem gjennom deres WS. Dvs. at konsumentens MQ-klient (som oftest m\xe5 v\xe6re egenutviklet) m\xe5 hente meldingen fra MQ og poste den til skytjenesten i stedet for \xe5 poste til eget API eller database."}),"\n"]}),"\n",(0,r.jsx)(n.p,{children:"Vi har laget meldingsk\xf8agenter for Linux og Windows. Dette er kode som i stor grad kan gjenbrukes, slik at det \xe5 utvikle en egen meldingsk\xf8agent er en overkommelig oppgave. Videre er det mulig \xe5 lage tjenester som konverterer fra det leverand\xf8ren tilbyr (f.eks. RSS) til v\xe5r meldingsk\xf8. Videre kan det v\xe6re hindre som i hvilken grad det i utgangspunktet tilbys, om hvor enkelt det er \xe5 hente meldinger, og lisensmessige aspekter for i hvilken grad man har lov til \xe5 lage egne integrasjoner. For skytjenester er man ofte helt begrenset til data tilbudt i deres standard API."})]})}function m(e={}){const{wrapper:n}={...(0,s.a)(),...e.components};return n?(0,r.jsx)(n,{...e,children:(0,r.jsx)(g,{...e})}):g(e)}},1151:(e,n,t)=>{t.d(n,{Z:()=>l,a:()=>i});var r=t(7294);const s={},a=r.createContext(s);function i(e){const n=r.useContext(a);return r.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function l(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(s):e.components||s:i(e.components),r.createElement(a.Provider,{value:n},e.children)}}}]);